            case 'password': const pattern=/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{6,}$/;
                if(!val) msg='рккрк╛рк╕рк╡рк░рлНркб ркЬрк░рлВрк░рлА ркЫрлЗ';
