// ---------------- Real-time Validation ----------------
document.addEventListener('DOMContentLoaded', ()=>{
    const form = document.getElementById('registerForm');
    const fields = ['name','email','password','mobile','district','taluka','village','role'];

    // Password hint below password box
    const passwordInput = document.getElementById('password');
    let passwordHint = passwordInput.nextElementSibling;
    if(!passwordHint || !passwordHint.classList.contains('password-hint')){
        passwordHint = document.createElement('div');
        passwordHint.classList.add('password-hint');
        passwordInput.parentNode.insertBefore(passwordHint, passwordInput.nextSibling);
    }
    passwordHint.style.fontSize = "14px";
    passwordHint.style.marginTop = "4px";

    fields.forEach(f=>{
        const input = form.querySelector(`[name="${f}"]`);
        if(input){
            input.addEventListener('input',()=>validateField(input));
            input.addEventListener('change',()=>validateField(input));
        }
    });

    function validateField(input){
        let val = input.value.trim();
        let msg = '';
        switch(input.name){
            case 'name': if(val==='') msg='рдирд╛рдо рдЖрд╡рд╢реНрдпрдХ рд╣реИ'; break;
            case 'email': 
                if(val==='') msg='рдИрдореЗрд▓ рдЖрд╡рд╢реНрдпрдХ рд╣реИ'; 
                else if(!/^[^@]+@[^@]+\.[^@]+$/.test(val)) msg='рдорд╛рдиреНрдп рдИрдореЗрд▓ рджрд░реНрдЬ рдХрд░реЗрдВ'; 
                break;
            case 'password':
                const pattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{6,}$/;
                if(val==='') msg='рдкрд╛рд╕рд╡рд░реНрдб рдЖрд╡рд╢реНрдпрдХ рд╣реИ';
                else if(val.length<6) msg='рдкрд╛рд╕рд╡рд░реНрдб рдХрдо рд╕реЗ рдХрдо 6 рдЕрдХреНрд╖рд░реЛрдВ рдХрд╛ рд╣реЛрдирд╛ рдЪрд╛рд╣рд┐рдП';
                else if(!pattern.test(val)) msg='рдкрд╛рд╕рд╡рд░реНрдб рдореЗрдВ рдмрдбрд╝реЗ, рдЫреЛрдЯреЗ рдЕрдХреНрд╖рд░ рдФрд░ рд╕рдВрдЦреНрдпрд╛ рд╢рд╛рдорд┐рд▓ рд╣реЛрдиреА рдЪрд╛рд╣рд┐рдП';
                else msg='valid';
                break;
            case 'mobile': 
                if(val==='') msg='рдореЛрдмрд╛рдЗрд▓ рдирдВрдмрд░ рдЖрд╡рд╢реНрдпрдХ рд╣реИ'; 
                else if(!/^\d{10}$/.test(val)) msg='рдорд╛рдиреНрдп 10-рдЕрдВрдХреЛрдВ рдХрд╛ рдореЛрдмрд╛рдЗрд▓ рдирдВрдмрд░ рджрд░реНрдЬ рдХрд░реЗрдВ'; 
                break;
            case 'district': if(val==='') msg='рдЬрд┐рд▓рд╛ рдЪреБрдиреЗрдВ'; break;
            case 'taluka': if(val==='') msg='рддрд╛рд▓реБрдХрд╛ рдЪреБрдиреЗрдВ'; break;
            case 'village': if(val==='') msg='рдЧрд╛рдВрд╡ рдЪреБрдиреЗрдВ'; break;
            case 'role': if(val==='') msg='рднреВрдорд┐рдХрд╛ рдЪреБрдиреЗрдВ'; break;
        }

        let errDiv = input.nextElementSibling;
        if(input.name==='password'){ errDiv = passwordHint; }
        if(errDiv){
            if(msg==='valid'){
                errDiv.style.color = 'green';
                errDiv.textContent = 'рдкрд╛рд╕рд╡рд░реНрдб рдлреЙрд░реНрдореЗрдЯ рд╕рд╣реА тЬЕ';
            } else {
                errDiv.style.color = 'red';
                errDiv.textContent = msg;
            }
        }
    }

    form.addEventListener('submit', e=>{
        let hasError=false;
        fields.forEach(f=>{
            const input=form.querySelector(`[name="${f}"]`);
            if(input && input.value.trim()===''){ 
                validateField(input); 
                hasError=true;
            }
        });
        if(hasError) e.preventDefault();
    });
});

